import { AntDesign } from '@expo/vector-icons'
import React from 'react'
import { ScrollView, StyleSheet, Text, TouchableOpacity, View, Image, TextInput } from 'react-native'
import { Card } from '.'
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState } from "react";

export function CardConta({ amount = 0.0, ...props }) {

	const [number, onChangeNumber] = React.useState(null);
	const [receita, setReceita] = useState("");
    const [gasto, setGasto] = useState("");

	const saveAndNavigate = async () => {

		const valor = {
			gasto: gasto,
			receita: receita
		}
		await asyncStorageSave(valor);
	   
		return navigation.navigate("Home");
	} 

const asyncStorageSave = async (user) => {
	try {
		
		await AsyncStorage.setItem('user', JSON.stringify(user));
		console.log('salvou no asyncstorage');
	} catch (error) {
		
		console.log('erro ao salvar no asyncstorage');
	}
}

	return (

        
		<Card {...props}>
			<Text style={[styles.label, styles.title]}>Saldo</Text>
			<Text style={[styles.label, styles.value]}>R$ {amount.toLocaleString('pt-BR')}</Text>
			<TextInput value={gasto} onChangeText={(e) => setGasto(e)} placeholder="Gasto" style={styles.input}></TextInput>
            <TextInput value={receita} onChangeText={(e) => setReceita(e)} secureTextEntry={true} placeholder="Receita" style={styles.input}></TextInput>			
			<ScrollView horizontal>
				<Button icon='qrcode' text='Pix' />
				<Button icon='barcode' text='Calcular' />
				
					
			</ScrollView>
			
           <TextInput
            style={styles.input}
            onChangeText={receita}
            placeholder="Receita"
            keyboardType="numeric"
           
          /> 
	 
          <TextInput
            style={styles.input}
            onChangeText={gasto}
            placeholder="Gasto"
            keyboardType="numeric"
          />
				
				
		</Card>
		
	)


function Button({ icon = 'qrcode', text = 'NA' }) {
	return (
		<TouchableOpacity style={styles.button}>
			<View style={styles.buttonImageContainer}>
				<AntDesign name={icon} size={24} color='black' />
			</View>
			<Text style={[styles.label, styles.buttonLabel]}>{text}</Text>
		</TouchableOpacity>
	)
}

function BarButton({ icon = 'qrcode', text = 'na' }) {
	return (
		<TouchableOpacity style={styles.barButton}>
			<AntDesign name={icon} size={24} color='black' style={styles.barButtonIcon} />
			<Text style={[styles.label, styles.barButtonLabel]}>{text}</Text>
		</TouchableOpacity>
	)
}

const styles = StyleSheet.create({
	label: {
		fontSize: 24,
		fontWeight: '600',
		marginBottom: 16,
	},
	title: {
		fontSize: 20,
	},
	value: {},
	button: {
		margin: 16,
		marginLeft: 8,
		marginRight: 8,
		alignItems: 'center',
	},
	buttonImageContainer: {
		marginBottom: 12,
		backgroundColor: '#e3e3e3',
		padding: 24,
		borderRadius: 50,
	},
	buttonLabel: {
		fontSize: 16,
	},
	barButton: {
		backgroundColor: '#e3e3e3',
		flexDirection: 'row',
		alignItems: 'center',
		padding: 24,
		margin: 16,
		borderRadius: 8,
	},
	barButtonIcon: {
		marginRight: 16,
	},
	barButtonLabel: {
		fontSize: 16,
		marginBottom: 0,
	},
	input: {
		height: 40,
		margin:30,
		width: 150,
		borderWidth: 2,
		padding: 20,
		fontSize:16,
		
	  },
	 
	  
	
}) }
